#include <iostream>
#include <vector>
#include <string>
using namespace std;

bool isValid(const vector<vector<char>>& board, int row, int col, char digit) {
    for (int i = 0; i < 9; ++i) {
        if (board[row][i] == digit) return false;
        if (board[i][col] == digit) return false;
        int subRow = (row / 3) * 3 + i / 3;
        int subCol = (col / 3) * 3 + i % 3;
        if (board[subRow][subCol] == digit) return false;
    }
    return true;
}

bool solve(vector<vector<char>>& board, int cell = 0) {
    if (cell == 81) return true;
    int row = cell / 9;
    int col = cell % 9;
    if (board[row][col] != '.') {
        return solve(board, cell + 1);
    }
    for (char digit = '1'; digit <= '9'; ++digit) {
        if (isValid(board, row, col, digit)) {
            board[row][col] = digit;
            if (solve(board, cell + 1)) return true;
            board[row][col] = '.';
        }
    }
    return false;
}

int main() {
    vector<vector<char>> board(9, vector<char>(9));
    for (int i = 0; i < 9; ++i) {
        string row;
        cin >> row;
        for (int j = 0; j < 9; ++j) {
            board[i][j] = row[j];
        }
    }

    if (solve(board)) {
        for (int i = 0; i < 9; ++i) {
            for (int j = 0; j < 9; ++j) {
                cout << board[i][j];
            }
            cout << '
';
        }
    } else {
        cout << "Sin solucion" << '
';
    }

    return 0;
}
